                    .appendTo( search_items )
                })
                if(data.length > 4){
                  jQuery("<li>")
                    .append("<a class='search-results-link'>" + "Show all results" + "</a> ").appendTo( search_items )
                }
                if(data.length > 0){
                  jQuery("#new-ticket-search")
